#pragma once

#include <cstddef>
#include <map>
#include <string>

namespace apache::thrift::compiler {

// The implementation of these extern values is provided by the file
// `templates.cc`, which is generated by `build_templates.cc` at build time.
namespace detail {

// Total number of templates
extern const std::size_t templates_size;

// Lengths of each template's name, e.g. `lang/xyz.mustache` -> 17
extern const std::size_t templates_name_sizes[];

// Names of each template, e.g. `lang/xyz.mustache`
extern const char* const templates_name_datas[];

// Lengths of each template's content, e.g. `...` -> 3
extern const std::size_t templates_content_sizes[];

// Raw string content of each template, e.g. `...`
extern const char* const templates_content_datas[];

} // namespace detail

using templates_map = std::map<std::string, std::string, std::less<>>;

// The implementation of this function is provided by `templates_map.cc`, which
// *is not* generated, but consumes the generated const values from
// `templates.cc`.
/**
 * Creates a mapping from Whisker source file paths within templates/ to
 * their text (source) content.
 *
 * This mapping is used to resolve the source code for template paths during
 * rendering.
 */
templates_map create_templates_by_path();

} // namespace apache::thrift::compiler
