template &lt;
    typename U = value_type,
    std::enable_if_t&lt;
        std::is_assignable&lt;reference_type, U&amp;&amp;&gt;::value &amp;&amp;
            std::is_constructible&lt;value_type, U&amp;&amp;&gt;::value,
        int&gt; = 0&gt;
